mapper.update:
update cart
set gAmount = #{gAmount}
where CARTID = #{cartId}

mapper.select:
SELECT *
FROM cart
WHERE USERNAME = #{userId}
  AND GCODE = #{gCode}
  AND GSIZE = #{gSize}
  AND GCOLOR = #{gColor}
  AND GAMOUNT = #{gAmount}

mapper.update:
update cart
set gAmount = gAmount+1
where gCode=#{gCode} and gColor=#{gColor} and gSize=#{gSize} and USERNAME=#{userId}
