mapper.select:
select USERNAME
from member
where username=#{username} and phoneNumber=#{phoneNumber}

mapper.update:
update member
set PASSWORD = #{newPassword},
    zip_Code = #{zip_Code},
    address = #{address},
    addr_Detail = #{addr_Detail}
where MEMBERID = #{memberId}

update member
set address = #{address},
    addr_Detail = #{addr_Detail},
    zip_Code = #{zip_Code},
    phoneNumber = #{phoneNumber}
where MEMBERID = #{memberId}

UPDATE SUJUNG.MEMBER
SET STATUS = 'active'
WHERE MEMBERID = #{memberId}
